import React from 'react';
import backgroundImage from './food.jpg';

const products = [
  // Add your product details here
  { id: 1, name: "Show Me Your Love", price: 9.99, imageUrl: './food.jpg' },
  { id: 2, name: "Camera Lens", price: 9.99, imageUrl: './food.jpg' },
  // ... other products
];

const HeroSection = ({ backgroundImage }) => {
  console.log('Image URL:', backgroundImage); // This will log the image URL to the console.

  return (
    <div className="relative bg-white overflow-hidden">
      {/* ... other parts of the component ... */}
      <div
        className="hidden lg:block lg:w-1/2"
        style={{
          backgroundImage: `url(${backgroundImage})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center center',
          height: '100vh' // Set a height for the div to ensure it's visible
        }}
      >
        {/* Empty div to hold background image */}
      </div>
    </div>
  );
};

const Customer = () => {
  return (
    <div className="bg-white">
      <HeroSection />
      <div className="max-w-2xl mx-auto py-16 px-4 sm:py-24 sm:px-6 lg:max-w-7xl lg:px-8">
        <div className="mt-6 grid grid-cols-1 gap-y-10 gap-x-6 sm:grid-cols-2 lg:grid-cols-4 xl:gap-x-8">
          {products.map((product) => (
            <div key={product.id} className="group relative">
              <div className="w-full min-h-80 bg-gray-200 aspect-w-1 aspect-h-1 rounded-md overflow-hidden group-hover:opacity-75 lg:h-80 lg:aspect-none">
                <img
                  src={product.imageUrl}
                  alt={product.name}
                  className="w-full h-full object-center object-cover lg:w-full lg:h-full"
                />
              </div>
              <div className="mt-4 flex justify-between">
                <div>
                  <h3 className="text-sm text-gray-700">
                    <span aria-hidden="true" className="absolute inset-0" />
                    {product.name}
                  </h3>
                  <p className="mt-1 text-sm text-gray-500">{product.name}</p>
                </div>
                <p className="text-sm font-medium text-gray-900">Â£{product.price}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Customer;
